mapper.select:
select r.roleId, r.name, r.description
from role r left join user_role ur on r.role_id = ur.role_id
where ur.user_id = #{userId}

select r.roleId, r.name, r.description
from role r left join user_role ur on r.role_id = ur.role_id
where ur.user_id != #{userId}

select r.roleId, r.name, r.description
from role r left join role_menu rm on r.role_id = rm.role_id
where rm.menu_id = #{menuId}

select r.roleId, r.name, r.description
from role r left join role_menu rm on r.role_id = rm.role_id
where rm.menu_id != #{menuId}
